// Menu component - main dude,  gets called in App.js with url path to select correct venue in airtable
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'
import React, {Component} from 'react';
// import './App.css';
import * as actions from './actions/actions.js'
import LoadingScreen from '../LoadingScreen/LoadingScreen.js'
import NotFound from '../NotFound/NotFound.js'
import Section from './Section.js'
import MenuItem from './MenuItem.js'

class Menu extends Component {

  componentWillMount() {
    const { getMenuData } = this.props
    getMenuData('Holla')
  }

  printMenu(menuSections) {
    if (menuSections.length === 0) {
      return <NotFound />
    } else {
      let menu = []
      console.log(menuSections);
      Object.values(menuSections).forEach(section => {
        if (typeof(section) === 'string') {
          // add SECTION to menu
          menu.push(
            <Section
              name={section}
            />
          )
        } else {
          // add ITEMS to menu under each SECTION
          section.forEach(item => {
            menu.push(
              <MenuItem item={item.fields} />
            )
          })
        }
      })
      return menu
    }
  }

  render() {
    const { bffRes, isLoading } = this.props

    return (
      <div>
        { isLoading ?
          <LoadingScreen />
          :
           <div className="Menu">
             {this.printMenu(bffRes)}
           </div>
         }
      </div>
    )
  }
}

const mapDispatchToProps = dispatch => bindActionCreators(actions, dispatch)

const mapStateToProps = state => ({
  router: state.router,
  bffRes: state.menu.bffRes,
  isLoading: state.menu.isLoading,
});

export default connect(mapStateToProps, mapDispatchToProps)(Menu);
