import React from 'react'
// import LinesEllipsis from 'react-lines-ellipsis'

class MenuList extends React.Component {

  render() {
    const { item, itemIndex, onClick } = this.props;
    // let trimmedName = item['Item Name']
    // if (trimmedName.length > 30) {
    //   trimmedName = trimmedName.substring(0,31).trim() + '...'
    // }
    // const clampedDesc = (
    //   <LinesEllipsis
    //     text={item['Item Description']}
    //     maxLine={3}
    //     ellipsis='...'
    //     basedOn='words'
    //     trimRight
    //   />
    // )

    return(
      <div className="listItem" key={ itemIndex } onClick={(e) => onClick(e)} >
        <span className="itemDesc">{ item['Item Description'] }</span>
        <span className="itemPrice">{ item.Price }</span>
      </div>
    )
  }
}

export default MenuList
